package ai;import l2p.extensions.scripts.Functions;import l2p.gameserver.ai.CtrlIntention;import l2p.gameserver.ai.Mystic;import l2p.gameserver.model.L2Character;import l2p.gameserver.model.L2Player;import l2p.gameserver.model.Reflection;import l2p.gameserver.model.L2World;import l2p.gameserver.model.instances.L2NpcInstance;import l2p.util.Rnd;/** * AI для Anakim Guard в 5 эпик квесте * ID = 32720 */public class AnakimGuard extends Mystic{	private L2NpcInstance lilithelite;				public AnakimGuard(L2Character actor)	{		super(actor);		AI_TASK_DELAY = 1000;		AI_TASK_ACTIVE_DELAY = 1000;	}		@Override	protected boolean thinkActive()	{		L2NpcInstance actor = getActor();		if(actor == null)			return true;					if(getIntention() == CtrlIntention.AI_INTENTION_ACTIVE)		{			if(lilithelite == null)				for(L2NpcInstance npc : L2World.getAroundNpc(actor, 1000, 200))					if(npc.getNpcId() == 32717)					{						npc.addDamageHate(actor, 0, 100);						lilithelite = npc;					}									if(lilithelite != null)				setIntention(CtrlIntention.AI_INTENTION_ATTACK, lilithelite);		}			return true;	}}